// Helper function to get icon URL by name
export const getDeviconUrl = (iconName: string): string => {
  const icon = deviconIcons.find(i => i.name === iconName);
  return icon ? icon.url : '';
};

// Helper function to get display name by icon name
export const getDeviconDisplayName = (iconName: string): string => {
  const icon = deviconIcons.find(i => i.name === iconName);
  return icon ? icon.displayName : iconName;
};

// Helper function to search icons by tag
export const searchDeviconsByTag = (tag: string): DeviconOption[] => {
  return deviconIcons.filter(icon => icon.tags.includes(tag.toLowerCase()));
};

// Helper function to search icons by name or display name
export const searchDeviconsByName = (searchTerm: string): DeviconOption[] => {
  const lowerSearch = searchTerm.toLowerCase();
  return deviconIcons.filter(icon => 
    icon.name.toLowerCase().includes(lowerSearch) || 
    icon.displayName.toLowerCase().includes(lowerSearch) ||
    icon.tags.some(tag => tag.toLowerCase().includes(lowerSearch))
  );
};
